import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c,e as p}from"./app-da3451da.js";const t={},d=p('<h2 id="nmap语法" tabindex="-1"><a class="header-anchor" href="#nmap语法" aria-hidden="true">#</a> Nmap语法</h2><p><code>Nmap【空格】【选项|多选项|协议】【空格】【目标】</code></p><h2 id="nmap命令" tabindex="-1"><a class="header-anchor" href="#nmap命令" aria-hidden="true">#</a> Nmap命令</h2><blockquote><p>1.全面扫描 <code>nmap -A 192.168.0.12</code></p></blockquote><blockquote><p>2.扫描指定网段 <code>nmap 192.168.0.0/24 或者 nmap 192.168.0.1-200（扫描1-200的主机）</code></p></blockquote><blockquote><p>3.简单扫描 <code>nmap 192.168.0.12</code></p></blockquote><blockquote><p>4.查看nmap版本号 <code>nmap -v</code></p></blockquote><blockquote><p>5.Ping扫描 <code>nmap -sP 192.168.0.0/24</code> 或者 <code>nmap -sP 192.168.0.12</code></p></blockquote><blockquote><p>6.无ping扫描 <code>nmap -P0 192.168.0.12</code></p></blockquote><blockquote><p>7.用 --packet-trace 查看协议是如何判断目标主机是都存活</p></blockquote><p>​ <code>nmap -p0 --packet-trace scanme.nmap.org</code></p><p>​ 可使用指定的TCP,UDP,IGMP协议，如：</p><p><code> nmap -p0 6,17,2 --packet-trace scanme.nmap.org</code></p><p>备注：（1）TCP：对应协议编号为6。</p><p>​ （2）ICMP:对应协议编号为1。</p><p>​ （3）IGMP：对应协议编号为2。</p><p>​ （4）UDP：对应协议编号为17。</p><blockquote><p>8.TCP SYN Ping扫描 <code>nmap -PS -v 192.168.0.12</code></p></blockquote><blockquote><p>可指定端口 <code>nmap -PS80,100-200 -v 192.168.0.12</code></p></blockquote><blockquote><p>9.TCP ACK Ping扫描 <code>nmap -PA -v 192.168.0.12</code></p></blockquote><blockquote><p>10.ICMP Ping Types扫描 PE选项打开该回声请求功能 <code>nmap -PE -v 192.168.0.12</code></p></blockquote><p>​ PP选项是ICMP时间戳ping扫描 <code>nmap -PP -v 192.168.0.12</code></p><p>​ PM选项可以进行icmp地址掩码ping扫描 <code>nmap -PM -v 192.168.0.12</code></p><blockquote><p>11.ARP Ping扫描 <code>nmap -PR 192.168.0.12</code></p></blockquote><blockquote><p>12.扫描列表 <code>nmap -sL 192.168.0.0/24</code></p></blockquote><blockquote><p>13.禁止反向域名解析 <code>namp -n -sL 192.168.0.0/24</code></p></blockquote><blockquote><p>14.反向域名解析 <code>nmap -R -sL *. 192.168.0.0/24</code></p></blockquote><blockquote><p>15.扫描IPv6地址 <code>nmap -6 fe80::20c:29ff:fee0:2e76</code></p></blockquote><blockquote><p>16.路由跟踪 <code>nmap --traceroute -v www.baidu.com</code></p></blockquote><blockquote><p>17.SCTP INIT Ping 扫描 <code>nmap -PY -v 192.168.0.12</code></p></blockquote><blockquote><p>18.扫描端口的方式 <code>nmap -p 80 192.168.0.12</code></p></blockquote><blockquote><p>19.发现具体而又有用的端口 <code>nmap --top-ports 100 192.168.0.12</code></p></blockquote><blockquote><p>20.TCP SYN 扫描 <code>nmap -sS 192.168.0.12</code></p></blockquote><blockquote><p>21.TCP连接扫描 <code>nmap -sT 192.168.0.12</code></p></blockquote><blockquote><p>22.UDP扫描 <code>nmap -sU -p 80-500 192.168.0.12</code></p></blockquote><blockquote><p>23.隐蔽扫描 <strong>-sN</strong>是Null扫描 <code>nmap -sN 192.168.0.12</code></p></blockquote><p>​ <strong>-sF</strong>是FIN扫描 <code>nmap -sF 192.168.0.12</code></p><p>​ <strong>-sX</strong>是Xmas扫描 <code>nmap -sX 192.168.0.12</code></p><blockquote><p>24.TCP ACK 扫描 <code>nmap -sA -v 192.168.0.12</code></p></blockquote><blockquote><p>25.TCP窗口扫描 <code>nmap -sW -v -F 192.168.0.12</code></p></blockquote><blockquote><p>26.TCP Maimon扫描 <code>nmap -sM -T4 192.168.0.12</code></p></blockquote><h2 id="从nmap识别端口状态" tabindex="-1"><a class="header-anchor" href="#从nmap识别端口状态" aria-hidden="true">#</a> <strong>从nmap</strong>识别端口状态</h2><blockquote><p>（1）Open：对外开放</p></blockquote><blockquote><p>（2）Closed：端口关闭状态</p></blockquote><blockquote><p>（3）Filtered：防火墙过滤或者网络堵塞</p></blockquote><blockquote><p>（4）Unfiltered：未被过滤，端口可访问，但是无法判断是开放还是关闭状态</p></blockquote><blockquote><p>（5）Open|Filtered：开放还是被过滤</p></blockquote><blockquote><p>（6）Closed|Filtered：不能确定是关闭还是被过滤，只可能出现在IPID Idle扫描中。</p></blockquote>',48),a=[d];function n(l,u){return e(),c("div",null,a)}const q=o(t,[["render",n],["__file","NMAP.html.vue"]]);export{q as default};
